let text = '';

if (typeof input.json === 'string') {
  text = input.json;
} else if (input.json.data) {
  text = input.json.data;
} else if (input.binary && input.binary.data) {
  text = Buffer.from(input.binary.data.data, 'base64').toString('utf-8');
} else {
  text = JSON.stringify(input.json);
}

const lines = text.split('\n').filter(l => l.trim());
if (lines.length === 0) {
  throw new Error('Empty CSV data');
}

const headers = lines[0].split(',').map(h => h.trim());

const result = [];
for (let i = 1; i < lines.length; i++) {
  const line = lines[i];
  if (!line.trim()) continue;

  const values = [];
  let current = '';
  let inQuotes = false;

  for (let char of line) {
    if (char === '"') {
      inQuotes = !inQuotes;
    } else if (char === ',' && !inQuotes) {
      values.push(current.trim());
      current = '';
    } else {
      current += char;
    }
  }
  values.push(current.trim());

  const obj = {};
  headers.forEach((h, idx) => {
    obj[h] = values[idx] || '';
  });

  result.push({ json: obj });
}

return result;

