const data = $input.first().json;

let analysis;
try {
  analysis = JSON.parse(data.response);
} catch (e) {
  analysis = {
    sentiment_score: 0,
    market_mood: "neutral",
    recommended_action: "hold",
    confidence: 0,
    reasoning: "LLM parse failed"
  };
}

return [{
  json: {
    date: data.date,
    ticker: data.ticker,
    price: parseFloat(data.close),
    analysis: analysis,
    indicators: {
      rsi: parseFloat(data.rsi || 0),
      macd_hist: parseFloat(data.macd_hist || 0),
      bb_position: parseFloat(data.bb_position || 0),
      volatility: parseFloat(data.volatility_7d || 0)
    }
  }
}];

// ========================================
// ‚úÖ Improved Trading Logic (Scoring + Risk Filters)
// ========================================

const item = $input.first().json;
const analysis = item.analysis || {};
const indicators = item.indicators || {};

const date = item.date;
const ticker = item.ticker || indicators.ticker || "N/A";
const price = item.price || indicators.close || item.close || 0;

// --- LLM signals (robust parsing) ---
const sentiment = Number(analysis.sentiment_score) || 0;        // -1..1
const impact = Number(analysis.news_impact) || 0;               // 0..1
const regRisk = Number(analysis.regulatory_risk) || 0;          // 0..1
const adoption = Number(analysis.adoption_signal) || 0;         // 0..1
const volRisk = Number(analysis.volatility_risk) || 0;          // 0..1
const conf = Number(analysis.confidence) || 0;                 // 0..1
const eventType = (analysis.event_type || "neutral").toLowerCase();

// --- Technical indicators ---
const rsi = Number(indicators.rsi) || 50;
const macdHist = Number(indicators.macd_hist) || 0;
const bbPos = Number(indicators.bb_position) || 0.5;           // 0..1
const vol7d = Number(indicators.volatility_7d) || 0;           // daily vol proxy

// --- Helpers ---
const clamp = (x, a, b) => Math.max(a, Math.min(b, x));
const sign = (x) => (x > 0 ? 1 : x < 0 ? -1 : 0);

// Normalize some features into [-1..1] / [0..1]
const rsiNorm = clamp((50 - rsi) / 25, -1, 1);                 // >0 => oversold bias
const bbNorm  = clamp((0.5 - bbPos) / 0.35, -1, 1);            // >0 => nearer lower band
const macdNorm = clamp(macdHist * 10, -1, 1);                  // scale to reasonable range
const volGate = clamp(1 - (vol7d / 0.06), 0, 1);               // 0..1 (0 if very volatile)

// Confidence-adjusted sentiment: reduces trading on weak LLM calls
const cas = sentiment * clamp(conf, 0, 1);

// --- Hard risk guards (reduce drawdown) ---
let decision = "hold";
let reason = "Filtered / neutral";
let flags = [];

// Emergency exits
if (rsi >= 88) {
  decision = "sell";
  reason = "Emergency exit: RSI extremely overbought";
  flags.push("rsi_emergency_sell");
  return [{ json: { date, ticker, price, decision, reason, score: -999, flags, debug: { sentiment, cas, conf, rsi, macdHist, bbPos, vol7d, impact, regRisk, adoption, volRisk, eventType } } }];
}

if (regRisk >= 0.85 && impact >= 0.5) {
  decision = "sell";
  reason = "High regulatory risk with meaningful news impact";
  flags.push("reg_risk_sell");
  return [{ json: { date, ticker, price, decision, reason, score: -888, flags, debug: { sentiment, cas, conf, rsi, macdHist, bbPos, vol7d, impact, regRisk, adoption, volRisk, eventType } } }];
}

// Trade avoidance filters (reduce noisy trades)
if (conf < 0.45) flags.push("low_confidence_hold");
if (impact < 0.25) flags.push("low_impact_hold");              // tiny news => usually noise
if (volRisk > 0.80) flags.push("llm_high_vol_hold");
if (vol7d > 0.06) flags.push("market_high_vol_hold");          // adjust threshold if needed

const blocked = (conf < 0.45) || (impact < 0.25) || (volRisk > 0.80) || (vol7d > 0.06);
if (blocked) {
  decision = "hold";
  reason = `Hold: ${flags.join(", ")}`;
  return [{ json: { date, ticker, price, decision, reason, score: 0, flags, debug: { sentiment, cas, conf, rsi, macdHist, bbPos, vol7d, impact, regRisk, adoption, volRisk, eventType } } }];
}

// --- Scoring model (hybrid: news + technical + risk penalties) ---
// Positive components
const newsScore = (cas * 35) + (adoption * 18) + (impact * 12);

// Technical components
const techScore = (rsiNorm * 14) + (macdNorm * 16) + (bbNorm * 10);

// Risk penalties (pushes to hold / sell)
const riskPenalty = (regRisk * 30) + ((1 - volGate) * 12);

// Event bias
let eventBias = 0;
if (eventType === "hack") eventBias -= 18;
if (eventType === "regulation") eventBias -= 10;
if (eventType === "adoption" || eventType === "partnership") eventBias += 8;
if (eventType === "macro") eventBias += 0;

// Final score
let score = newsScore + techScore + eventBias - riskPenalty;

// Mild ‚Äúanti-chase‚Äù rule: don‚Äôt buy if already overbought or at top band
if (rsi > 72) score -= 12;
if (bbPos > 0.85) score -= 10;

// Mild ‚Äúdip-buy preference‚Äù: if oversold and sentiment not negative
if (rsi < 35 && cas > -0.15) score += 8;

// --- Decision thresholds (tuneable) ---
// Higher buy threshold => fewer trades => often higher Sharpe
const BUY_TH = 28;
const SELL_TH = -18;

if (score >= BUY_TH) {
  decision = "buy";
  reason = "Score-based buy: aligned news+tech with acceptable risk";
} else if (score <= SELL_TH) {
  decision = "sell";
  reason = "Score-based sell: negative or high-risk setup";
} else {
  decision = "hold";
  reason = "Hold: score not strong enough";
}

// Extra safety: if RSI high and score only slightly positive => hold
if (decision === "buy" && rsi > 70 && score < (BUY_TH + 6)) {
  decision = "hold";
  reason = "Hold: late entry risk (high RSI)";
  flags.push("late_entry_hold");
}

return [{
  json: {
    date,
    ticker,
    price,
    decision,
    reason,
    score: Number(score.toFixed(2)),
    // Keep useful outputs for debugging/backtest inspection
    llm: {
      sentiment: Number(sentiment.toFixed(3)),
      cas: Number(cas.toFixed(3)),
      confidence: Number(conf.toFixed(3)),
      impact: Number(impact.toFixed(3)),
      regulatory_risk: Number(regRisk.toFixed(3)),
      adoption_signal: Number(adoption.toFixed(3)),
      volatility_risk: Number(volRisk.toFixed(3)),
      event_type: eventType
    },
    tech: {
      rsi: Number(rsi.toFixed(2)),
      macd_hist: Number(macdHist.toFixed(5)),
      bb_position: Number(bbPos.toFixed(3)),
      volatility_7d: Number(vol7d.toFixed(5))
    },
    flags
  }
}];

